    private void rdoAlunoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rdoAlunoActionPerformed

        if(rdoAluno.isSelected()){
            txtCurso.setVisible(true);
            txtDisciplina.setVisible(false);
        }

    }//GEN-LAST:event_rdoAlunoActionPerformed

    private void rdoProfessorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rdoProfessorActionPerformed
        if(rdoProfessor.isSelected()){
            txtDisciplina.setVisible(true);
            txtCurso.setVisible(false);
        }
    }//GEN-LAST:event_rdoProfessorActionPerformed

    private void btnAtualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAtualizarActionPerformed

        if(rdoConsultaAluno.isSelected()){
            Aluno a = new Aluno();
            a.setId(Integer.parseInt(txtID.getText()));
            a.setNome(txtNome.getText());
            a.setIdade(Integer.parseInt(txtIdade.getText()));
            rdoAluno.setSelected(true);
            a.setCurso(txtCurso.getText());
           

            alunoDAO aDAO = new alunoDAO();
            aDAO.atualizar(a);

            limparFormulario();
        }
        else if(rdoConsultaProfessor.isSelected()){
            Professor p = new Professor();
            p.setId(Integer.parseInt(txtID.getText()));
            p.setNome(txtNome.getText());
            p.setIdade(Integer.parseInt(txtIdade.getText()));
            rdoProfessor.setSelected(true);
            p.setDisciplina(txtDisciplina.getText());
           

            professorDAO pDAO = new professorDAO();
            pDAO.atualizar(p);

            limparFormulario();
        }
        
        
    }//GEN-LAST:event_btnAtualizarActionPerformed

    private void btnConsultarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConsultarActionPerformed
       
        if(rdoConsultaAluno.isSelected()){
            alunoDAO aDAO = new alunoDAO();
            int id = Integer.parseInt(txtID.getText());

            Aluno a = aDAO.consulta(id);

            if(a == null){
                limparFormulario();
                JOptionPane.showMessageDialog(this, "Aluno(a) n達o encontrado(a)");
            }
            else{
                txtIDconsulta.setText(String.valueOf(a.getId()));
                txtNome.setText(a.getNome());
                txtIdade.setText(String.valueOf(a.getIdade()));

                rdoAluno.setSelected(true);

                txtCurso.setText(a.getCurso());
                txtDisciplina.setVisible(false);
                txtCurso.setVisible(true);
                }   
            
        }
        else if (rdoConsultaProfessor.isSelected()){
            professorDAO pDAO = new professorDAO();
            int id = Integer.parseInt(txtID.getText());

            Professor p = pDAO.consulta(id);

            if(p == null){
                limparFormulario();
                JOptionPane.showMessageDialog(this, "Professor(a) n達o encontrado(a)");
            }
            else{
                txtIDconsulta.setText(String.valueOf(p.getId()));
                txtNome.setText(p.getNome());
                txtIdade.setText(String.valueOf(p.getIdade()));

                rdoProfessor.setSelected(true);

                txtDisciplina.setText(p.getDisciplina());
                txtDisciplina.setVisible(true);
                txtCurso.setVisible(false);

                }
        }
    }//GEN-LAST:event_btnConsultarActionPerformed

    private void rdoConsultaAlunoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rdoConsultaAlunoActionPerformed
        if(rdoConsultaAluno.isSelected()){
            limparFormularioMenosID();
            rdoAluno.setSelected(true);
            txtDisciplina.setVisible(false);
            txtCurso.setVisible(true);
        }
        
    }//GEN-LAST:event_rdoConsultaAlunoActionPerformed

    private void rdoConsultaProfessorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rdoConsultaProfessorActionPerformed
        if(rdoConsultaProfessor.isSelected()){
            limparFormularioMenosID();
            rdoProfessor.setSelected(true);
            txtDisciplina.setVisible(true);
            txtCurso.setVisible(false);
        }
        
    }//GEN-LAST:event_rdoConsultaProfessorActionPerformed

    private void btnExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExcluirActionPerformed
        
        if(rdoConsultaAluno.isSelected()){
            int resposta = JOptionPane.showConfirmDialog(null, "Deseja realmente excluir este(a) aluno(a)?", "Exclus達o", JOptionPane.YES_NO_OPTION);
            if (resposta == JOptionPane.YES_OPTION){

                alunoDAO aDAO = new alunoDAO();
                aDAO.excluir(Integer.parseInt(txtID.getText()));
                limparFormulario();

            }
        }
        else if(rdoConsultaProfessor.isSelected()){
            int resposta = JOptionPane.showConfirmDialog(null, "Deseja realmente excluir este(a) professor(a) ?", "Exclus達o", JOptionPane.YES_NO_OPTION);
            if (resposta == JOptionPane.YES_OPTION){

                professorDAO pDAO = new professorDAO();
                pDAO.excluir(Integer.parseInt(txtID.getText()));
                limparFormulario();

            }
        }
    }//GEN-LAST:event_btnExcluirActionPerformed

    private void limparFormulario(){
        txtID.setText("");
        txtIDconsulta.setText("");
        txtNome.setText("");
        btnGrpIdentificador.clearSelection();
        txtIdade.setText("");
        txtIdade.setText("");
        txtCurso.setText("");
        txtDisciplina.setText("");
    }
    
    private void limparFormularioMenosID(){
        txtIDconsulta.setText("");
        txtNome.setText("");
        btnGrpIdentificador.clearSelection();
        txtIdade.setText("");
        txtIdade.setText("");
        txtCurso.setText("");
        txtDisciplina.setText("");
    }
    
    
